CREATE TABLE IF NOT EXISTS PRODUCTS (id INTEGER auto_increment PRIMARY KEY , title VARCHAR(50), description VARCHAR(200), price DOUBLE, userId INTEGER ) CHARSET = utf8;
CREATE TABLE IF NOT EXISTS  USERS (id INTEGER auto_increment PRIMARY  KEY , name VARCHAR (50), email VARCHAR (50), phoneNumber VARCHAR(100)) CHARSET = utf8;
CREATE TABLE IF NOT EXISTS ACCOUNTS (id INTEGER  auto_increment PRIMARY  KEY, accountsNumber INTEGER NOT NULL, amount DOUBLE, ownerId INTEGER NOT NULL) CHARSET = utf8;
CREATE TABLE IF NOT EXISTS BASKET (id INTEGER auto_increment PRIMARY KEY, userId INTEGER NOT NULL, productId INTEGER NOT NULL, count INTEGER NOT NULL) CHARSET = utf8;
CREATE TABLE IF NOT EXISTS checkTable (id INTEGER auto_increment PRIMARY KEY, userId INTEGER NOT NULL, productId INTEGER NOT NULL, count INTEGER NOT NULL) CHARSET = utf8;